interface Book {
  id: number;
  title: string;
  author: string;
  genre: string;
  rating: number;
  totalCopies: number;
  availableCopies: number;
  description: string;
  coverColor: string;
  coverUrl: string;
  videoUrl: string;
  summary: string;
  isLoanedBook?: boolean;
}

interface AuthCredentials {
  fullName: string;
  email: string;
  password: string;
  universityId: number;
  universityCard: string;
}

interface EmailParams {
  to_name?: string;
  from_name?: string;
  user_name?: string;
  user_email?: string;
  message?: string;
  [key: string]: unknown; // Allow additional custom parameters
}
